import React, {Component} from 'react';
import Main from './Main'

class MainComponent extends Component{
    constructor(props)
    {
        super(props);
        this.state = {
            filters : [],
            selectedFilter: {},
            input: "",
            response : ""
        }
        this.handleFilterChange = this.handleFilterChange.bind(this);
        this.updateInputValue = this.updateInputValue.bind(this);
        this.handleSubmitAction = this.handleSubmitAction(this);
    }
    componentDidMount()
    {
        fetch('https://localhost:44363/api/metrics')
        .then(res => res.json())
        .then(state => this.setState({filters: state}));
    }

    handleFilterChange(e)
    {
        this.setState({selectedFilter : e.target.id});
    }

    handleSubmitAction()
    {
        if(this.state.input != null){
        fetch('https://localhost:44363/api/metrics?' + this.state.selectedFilter.id,
        {   
            method:'POST',
            body:this.state.input
        }) 
        .then(res => this.setState({response: res.text}));
    }
    }

    updateInputValue(e)
    {
        this.setState({input: e.target.value});
    }

    render(){
        return(<Main filters={this.state.filters} onFilterChange={this.handleFilterChange} onInputUpdate={this.updateInputValue} onSubmit={this.handleSubmitAction} response = {this.state.response}/>);
    }
}

export default MainComponent;
