import React, {Component} from 'react'

class Main extends Component{
    constructor(props)
    {
        super(props);   
    }
    handleChange()
    {
        const filter = this.dropdown.value;
        this.props.onFilterChange(filter);
    }
    render(){
        const filters = this.props.filters.map(filter => {
            return <option value={filter} key={filter.id} onChange={this.props.onFilterChange}>{filter.description}</option>
        })
        return(<div>
            <input type = "text" onChange={this.handleChange}></input>
            <select ref={(ref) => this.dropdown = ref}  id ='filters'>
                {filters}   
            </select>
            <button onClick={this.props.onSubmit}></button>
        <p>{this.props.response}</p>
        </div>)
    }
}

export default Main;
