import React, {Component} from 'react'
import 'bootstrap/dist/css/bootstrap.min.css';

class Main extends Component{
    constructor(props)
    {
        super(props);   
        this.handleChange = this.handleChange.bind(this);
        this.handleTextChange = this.handleTextChange.bind(this);
    }
    handleChange()
    {
        const filter = this.refs.dropdown.value;
        this.props.onFilterChange(filter);
    }
    handleTextChange()
    {
        const text = this.refs.text.value;
        this.props.onInputUpdate(text);
    }
    render(){
        const filters = this.props.filters.map(filter => {
            return <option value={filter.id} key={filter.id}>{filter.description}</option>
        })
        return(
        <div>
            <div className="form-group">
            <input className="form-control" rows="5" ref ="text" onChange={this.handleTextChange}></input>
            </div>
            <div className="form-group">
            <select ref='dropdown' className="form-control"  onChange={this.handleChange}  id ='filters'>
                {filters}   
            </select>
            </div>
            <button className="btn btn-primary" onClick={this.props.onSubmit}>Calculate</button>
            <p>{this.props.response}</p>
        </div>
        )
    }
}

export default Main;
