import React, {Component} from 'react'

class Main extends Component{
    constructor(props)
    {
        super(props);   
        this.handleChange = this.handleChange.bind(this);
        this.handleTextChange = this.handleTextChange.bind(this);
    }
    handleChange()
    {
        const filter = this.refs.dropdown.value;
        this.props.onFilterChange(filter);
    }
    handleTextChange()
    {
        const text = this.refs.text;
        this.props.onInputChange(text);
    }
    render(){
        const filters = this.props.filters.map(filter => {
            return <option value={filter.id} key={filter.id}>{filter.description}</option>
        })
        return(<div>
            <input type = "text" ref ="text" onChange={this.handleTextChange}></input>
            <select ref='dropdown'  onChange={this.handleChange}  id ='filters'>
                {filters}   
            </select>
            <button onClick={this.props.onSubmit}></button>
        <p>{this.props.response}</p>
        </div>)
    }
}

export default Main;
